{"version":3,"sources":["header-scrolling.js"],"names":["$","window","on","scroll","scrollTop","logo","nav","menu","addClass","removeClass","jQuery"],"mappings":"CAMA,SAAUA,GACR,aA2BAA,EAAEC,QAAQC,GAAG,SAzBb,WACE,IAAIC,EAASH,EAAEC,QAAQG,YAInBC,EAAYL,EAAE,YACdM,EAAYN,EAAE,WACdO,EAAYP,EAAE,mBAGdG,GARY,IAUdE,EAAKG,SAAS,aACdF,EAAIE,SAAS,aACbD,EAAKC,SAAS,eAIdH,EAAKI,YAAY,aACjBH,EAAIG,YAAY,aAChBF,EAAKE,YAAY,gBAvBvB,CA8BGC","file":"../header-scrolling.js","sourcesContent":["/**\n *\n * Header changes when the users scrolls\n *\n */\n\n(function($) {\n  'use strict';\n\n  function headerScrolling() {\n    var scroll = $(window).scrollTop();\n    var threshold = 50;\n\n    // Header elements\n    var logo      = $('.js-logo');\n    var nav       = $('.js-nav');\n    var menu      = $('.js-menu-toggle');\n\n    // When the user scrolls and reaches the threshold.\n    if (scroll >= threshold) {\n      // Make header sticky.\n      logo.addClass('is-sticky');\n      nav.addClass('is-sticky');\n      menu.addClass('is-sticky');\n    }\n    else {\n      // Make header not sticky.\n      logo.removeClass('is-sticky');\n      nav.removeClass('is-sticky');\n      menu.removeClass('is-sticky');\n    }\n  }\n\n  // Run function when the user scrolls.\n  $(window).on('scroll', headerScrolling);\n\n})(jQuery);\n"]}